const dropArea = document.querySelector('#drop__area');
const progressBar = document.querySelector('#progress-bar');
const placeholder =  document.querySelector('.signin__figure');
const reset = document.querySelector('#reset');
const gallery = document.querySelector("#gallery");
const inputFile = document.querySelector('.signin__input_file');


let filesDone = 0;
let filesToDo = 0;

['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
  dropArea.addEventListener(eventName, preventDefaults, false)
});
function preventDefaults (e) {
  e.preventDefault()
  e.stopPropagation()
};

['dragenter', 'dragover'].forEach(eventName => {
  dropArea.addEventListener(eventName, highlight, false)
});

['dragleave', 'drop'].forEach(eventName => {
  dropArea.addEventListener(eventName, unhighlight, false)
});

function highlight(e) {
  placeholder.classList.add('highlight')
  gallery.classList.add('highlight')
};

function unhighlight(e) {
  placeholder.classList.remove('highlight')
  gallery.classList.remove('highlight')
};

dropArea.addEventListener('drop', handleDrop, false);

inputFile.addEventListener('change', function(){
  let files = this.files;
  handleFiles(files)
}, false);

function handleDrop(e) {
  let dt = e.dataTransfer
  let files = dt.files
  handleFiles(files)
};

function handleFiles(files) {
  files = [...files];
  initializeProgress(files.length)
  placeholder.style.display = 'none';
  progressBar.style.display = 'block';
  files.forEach(previewFile)
  files.forEach(item => {
    if (item.size > 5242880){
      console.error("it's to much");
    }
    else{
      console.log('ok');
    }
  });
};

function previewFile(file) {
  let reader = new FileReader()
  reader.readAsDataURL(file)
  reader.onloadend = function() {
    let img = document.createElement('img')
    img.src = reader.result
    gallery.style.display = 'block';
    gallery.append(img)
    progressDone();
  };
  reset.addEventListener('click', function() {
    let images = gallery.querySelectorAll('img')
    images.forEach(item => {
      item.remove();
    })
    progressBar.style.display = 'none';
    gallery.style.display = 'none';
    placeholder.style.display = 'block';
  });
  
}

function initializeProgress(numfiles) {
  progressBar.value = 0
  filesDone = 0
  filesToDo = numfiles
}

function progressDone() {
  filesDone++
  progressBar.value = filesDone / filesToDo * 100
}

let swiper = null;
const menu = ["Главная","О нас", "Как это работает","Отслеживание трека/Новости","Faq"];

function swiperInit() {
  if(!swiper){
    swiper = new Swiper('.swiper-container', {
      // Optional parameters
      direction: 'horizontal',
      loop: false,
      slidesPerView: 1,
      mousewheel: true,
      allowTouchMove: false,

      // If we need pagination
      pagination: {
        el: '.nav__list.pages',
        clickable: true,
        renderBullet: function (index, className) {
            return '<span class="' + className + '">' + (menu[index]) + '</span>';
          },
        },  
      // And if we need scrollbar
      scrollbar: { 
        el:'.swiper-scrollbar',
        dragSize: 20,
      },
    });

    const bullets = document.querySelectorAll('.swiper-pagination-bullet'),
          progressbar = document.querySelector('.progressbar.swiper-scrollbar'),
          active = 'swiper-pagination-bullet-active';

    swiper.on('slideChange', function() {
      if (swiper.pagination.bullets[1].classList.contains(active) || swiper.pagination.bullets[2].classList.contains(active)) {
        progressbar.classList.add('light');
        bullets.forEach(item =>{
          item.classList.add('light')
        })
      }else{
        progressbar.classList.remove('light')
        bullets.forEach(item =>{
          item.classList.remove('light')
        })
      }
    });
  };
};

function checkSize () {
  let windowWidth = $(window).width();
  if (windowWidth > 768) {
    swiperInit();
  }else{
    if (swiper) {
      swiper.destroy(true, false);
      swiper = null;
    }
  }
}

$(document).ready(function(){
  checkSize();
});

$(window).on('resize', function() {
  checkSize();
});


