function showPopup(buttons, parent) {
  buttons.forEach(item => {
    item.addEventListener('click', (e) => {
      e.preventDefault();
      
      $('.popup').fadeOut();
      $(item).closest(parent).find('.popup').fadeIn();
    });
  });
};

